import React, { useContext } from 'react'
import {RoomContext} from '../context';
import {useParams} from "react-router-dom";
import SingleRoomComp from "../components/SingleRoom"


export default function SingleRoom() {
    let {slug} = useParams();
    const {rooms} = useContext(RoomContext)
    console.log(useParams().slug)
    let SingleRoom = rooms.map(SingleRoom=>{
        if(SingleRoom.slug.match(useParams().slug)) return <SingleRoomComp room={SingleRoom}></SingleRoomComp>
    })
    return (
        <div>
            {slug}
            {SingleRoom}
        </div>
    )
}
