import React, { Component } from 'react'
import {RoomContext} from "../context"
import data from "../data";
import LoadingComp from "./Loading";
import Title from "./Title";
import {Link} from "react-router-dom";

export default class FeaturedRooms extends Component {
    static contextType = RoomContext;

    render() {
        const {rooms, featuredRooms, sortedRooms, Loading} = this.context;
        console.log(rooms, featuredRooms)

        let roomsComp = rooms.map((item, key)=>{
            return <div key={key}>
                <h4>hi</h4>
            </div>
        })



        return (

            <div className="featured-rooms-center">
                <Title title="featured rooms"></Title>
            {Loading ? <LoadingComp></LoadingComp> : 

              rooms.map((item,key)=>{
                  return <Link to={`/rooms/${item.slug}`}><div key={key} className="featured-rooms" style={{backgroundImage:`url(${item.images[0]})`, backgroundSize:`cover`}}>
                            <h4>{item.name}</h4>
                         </div>
                         </Link>
              })

            }

            </div>
        )
    }
}
