    const {
        handleChange,
        type, 
        capacity,
        price,
        minPrice,
        maxPrice,
        minSize,
        maxSize,
        breakfast,
        pets
    } = context
    console.log(context);
    return (
       <section className="filter-container">
           <Title title="search rooms"></Title>
           <form className="filter-form">
               <div classnName="form-group">
                   <label for="roomSize">Room Size</label>
                   <input type="number" className="" id="roomSize" value={maxSize}></input>
               </div>
           </form>
       </section>
    )
}
