import React from 'react'
import {Link} from "react-router-dom";
import PropTypes from "prop-types";
import defaultImg from "../images/room-1.jpeg";


export default function Rooms({id, rooms}) {
    const {name, images, price, slug } = rooms;

    let defaultImage = `../images/room-${id}.jpeg`;
    return (
        <Link to={`/rooms/${rooms.slug}`} key={id} style={{textDecoration:"none", color:"#222"}}>
        <section key={id} className="room">
            <div className="" className="img-container">
                <span className="price-top"><h6>${rooms.price}</h6><p>per night</p></span>
                <img src={rooms.images[0] || defaultImg} style={{width:"100%", display:"block"}}/>
                <div className="btn-primary room-link">Featured</div>
            </div>
            <p className="room-info">{rooms.name}</p>

        </section>
        </Link>
    )
}
