export default class SingleRoom extends Component {
    constructor(props){
        super(props)
        this.state = {
            slug: this.props.match.params.slug,
            defaultBackground: {defaultBackground}
        }

    }


    static contextType = RoomContext;
    render() {

        const {getRoom} = this.context;

        const room = getRoom(this.state.slug)

        console.log({room});


       if(!room){
           return <div className="error">No such room</div>

       } else {
        return (
            <div>

                <Hero hero="roomsHero">
                <Banner title={room.name}>
                     <Link to="/rooms/"><button className="btn-primary">Home</button></Link>
                 </Banner>
                </Hero>
                <div className="single-room">
            <div className="single-room-images">
                {room.images.map(images =>{
                   return <img src={images} alt="" key={images.indexOf({images})}></img>
                })}
            </div>
            {/* <div className="room-info">
                <h3>Info</h3>
                {room.map(room=>{

                })}
            </div> */}
            <div className="room-extras">
                <h3>Extras</h3>
                <ul>
                    {room.extras.map(extras=>{
                        return <li className="extras">{extras}</li>
                    })}
                </ul>
            </div>
            <div className="desc">
                <h3>{room.name}</h3>
                <p>{room.description}</p>
            </div>

        </div>
            </div>
        )
    }
}
