import React, { Component } from 'react'
import items from './data';
const RoomContext = React.createContext();
//
console.log(typeof items)
class RoomProvider extends Component {

    state = {
        rooms: [],
        sortedRooms: [],
        featuredRooms:[],
        loading: true,
        type: 'all',
        capacity: 2,
        price: 400, 
        minPrice: 0,
        maxPrice: 0,
        size: 1000,
        minSize: 0,
        maxSize: 0,
        breakfast: false,
        pets: true

    };

    componentDidMount(){
        let rooms = this.formatData(items)
        // let sortedRooms = this.formatData(this.sortedRooms())
        let featuredRooms = rooms.filter(rooms=>rooms.featured==true);
        let maxPrice = Math.max(...rooms.map(item=>item.price));
        let maxSize = Math.max(...rooms.map(item=>item.size));
        this.setState({
            rooms,
            featuredRooms,
            sortedRooms: [],
            loading: false,
            price: maxPrice,
            maxPrice: maxPrice,
            size: maxSize,
            maxSize: maxSize
        }, this.sortedRooms)
        // console.log(sortedRooms)
        console.log(featuredRooms)
    }


    formatData(items) {
        let tempItems = items.map(item=>{
            let id = item.sys.id;
            let images = item.fields.images.map(images=>images.fields.file.url);
            let rooms = {...item.fields, images, id};
            return rooms;
        });
        return tempItems;

    }

    sortedRooms() {
        let {price, pets, capacity, size, breakfast, type, minSize, maxSize} = this.state;
        let tempRooms = [...this.state.rooms]
        capacity = parseInt(capacity);
        minSize = parseInt(minSize);
        maxSize = parseInt(maxSize);
        if( type != 'all') {
            tempRooms = tempRooms.filter(items => {return items.price <= price && items.pets === pets && items.capacity >= capacity && items.size <= maxSize && items.size >= minSize && items.breakfast === breakfast && items.type === type} );
        } else {
       tempRooms = tempRooms.filter(items =>  items.price <= price && items.pets === pets && items.capacity >= capacity && items.size <= maxSize && items.size >= minSize && items.breakfast === breakfast );
        }
        tempRooms.map(items=> parseInt(items.capacity))
        this.setState({
            sortedRooms: tempRooms
        })


    }



    getRoom = slug => {
        let tempRooms = [...this.state.rooms];
        const room = tempRooms.find(room => room.slug===slug);
        return room;
    }

    handleChange = event => {
        const target = event.target;
        const name = event.target.name;
        const value =  event.type === 'checkbox' ? target.checked : event.target.value;
        console.log(name, value)
        if(name === 'minSize' && value == "" || value === nil || value === null) {
            this.setState({
                minSize: 0
            }, this.sortedRooms)
        }
        this.setState({
            [name]: value 
        }, this.sortedRooms);
    }

    render() {
        return (
            <RoomContext.Provider value={{...this.state, getRoom:this.getRoom, handleChange: this.handleChange}}>
                {this.props.children}
            </RoomContext.Provider>
        )
    }
}



const RoomConsumer = RoomContext.Consumer;

export function withRoomConsumer(Component) {
    return function ConsumerWrapper(props) {
        return <RoomConsumer>
            {value => <Component {...props} context={value}/>}
        </RoomConsumer>
    }
}

export {RoomProvider, RoomConsumer, RoomContext}
